import React from 'react';
//import { connect } from "react-redux";

//import {addUserEntityThunk, addUserThunk,fetchAllUsersThunk } from "../../thunks";
class ConfirmAccount extends React.Component {

	componentDidMount(){
		var location=window.location.href;
		var splitToken=location.split('token:');
var token=splitToken[0];
		var test=fetch(`http://localhost:8080/confirm-account`, {
			method: 'POST',
			body:token,
			headers:{
				"Access-Control-Allow-Origin":'http://localhost:8080',
				"Access-Control-Allow-Methods": "GET, OPTIONS, POST, PUT"
			}
		}).then(response => {
			return response
		}).catch(err => {
			this.setState({error: err});
		});
console.log(test);
	}

	render() {
		return (
			 <div className="center">
			 <div className="text-center">
			 
			 <form className="form-signin" onSubmit={this.handleSubmit}>
			 <h2 className="h3 mb-3 font-weight-normal">Congardulation</h2>
			 <br/>
			   
			  
			   
				
			   <button className="btn btn-primary" to="/home">Login</button>
			 </form>
		
			 </div>
		   </div>
		)
	}

}

export default ConfirmAccount;
