import React from 'react';
import { connect } from "react-redux";

import {addUserEntityThunk, addUserThunk,fetchAllUsersThunk } from "../../thunks";
class UploadFile extends React.Component {
	
	constructor(props) {
		super(props);
		this.state = {file: '', message: '',email:"",userEntity:[]
	};
	}
	onEmailChange=(event)=>{
		this.setState({
			email:event.target.value
		})
	}
	onFileChange = (event) => {
		this.setState({
			file: event.target.files[0]
		});
	}
	
	uploadFileData = (event) => {
		event.preventDefault();
		this.setState({message: ''});
		
		
		  this.postFile();
	  
	}


	validateUser = () => {
		//check is the username length great than 5 and not duplicate username in database
		const users=this.props.allUsers;
	console.log(users)
	  //let isValidName = true;
	  var userEn=[];
	  console.log(this.state.email)
	  var i=this.state.email;
	  userEn=users.filter(function(item){
		  console.log(item.email,i)
		return  item.email === i;
	 }).map(function({username,email,is_Enabled,password}){
		return {username,email,is_Enabled,password};
	  })
	  
	 this.setState(
		 {
			 userEntity:userEn
		 }
	 )
	console.log(this.state.userEntity,userEn);
	 this.props.addUserEntity(userEn[0]);
	// userEn=users[i].email;
	
	
	  
	  
	};
	postFile(){
		var fileUpload;
		let data = new FormData();
		data.append('file', this.state.file);
		console.log(this.state.file);
		/*for(var pair of data.entries()){
			console.log(pair[0]+','+pair[1]);
			fileUpload += pair[1];
		};*/
		console.log(fileUpload);
	//	var ba_token="Bearer "+token;
		fetch(`http://localhost:8080/upload`, {
			method: 'POST',
			body:data,
			headers:{
				"Access-Control-Allow-Origin":'http://localhost:8080',
				"Access-Control-Allow-Methods": "GET, OPTIONS, POST, PUT"
			}
		}).then(response => {

			this.setState({message: "File successfully uploaded"});
		}).catch(err => {
			this.setState({error: err});
		});

	}
	handleSubmit = (e) => {
		e.preventDefault();
		this.validateUser();
	  };
	
	render() {
		return (
			 <div className="center">
			 <div className="text-center">
			 
			 <form className="form-signin" onSubmit={this.handleSubmit}>
			 <h2 className="h3 mb-3 font-weight-normal">Verification Process</h2>
			 <br/>
			   
			  
			   <div>
			   Upload a File:{" "}
				 <input
				 className="form-control"
				   type="file"
				   value={this.file}
				   name="file"
				   onChange={this.onFileChange}
				   required
				 ></input>
				 <br/>
				 <button className="btn btn-secondary" disabled={!this.state.file} onClick={this.uploadFileData}>Upload</button>
			
			   </div>
				
				<br/>
				<div>
			   email:{" "}
				 <input
				 className="form-control"
				   type="email"
				   value={this.email}
				   name="email"
				   onChange={this.onEmailChange}
				   required
				 ></input>
			   </div>
			   <br/>
			   <button className="btn btn-primary" to="/Confirm">Click here to verify</button>
			 </form>
		
			 </div>
		   </div>
		)
	}

}

const mapState = (state) => {
	console.log(state)
	return {
	  allUsers: state.allUsers[0],
	  message:state.allUsers[1].message
	};
  };
  const mapDispatch = (dispatch, ownProps) => {
	return {
	  fetchAllUsers: () => dispatch(fetchAllUsersThunk()),
	  
	  addUser: (User) => dispatch(addUserThunk(User, ownProps)),
	  addUserEntity: (userEntity) => dispatch(addUserEntityThunk(userEntity, ownProps)),
	};
  };
export default connect(mapState, mapDispatch)(UploadFile);
