import React from 'react';
import { connect } from "react-redux";

import {addUserEntityThunk, addUserThunk,fetchAllUsersThunk } from "../../thunks";
class ConfirmAccount extends React.Component {

	componentDidMount(){
		var location=window.location.href;
		var splitToken=location.split('confirm-account?token=');
var token=splitToken[1];
var url="http://localhost:8080/confirm-account?token="+token;
console.log(location,token)
		var test=fetch(url, {
			method: 'POST'
		}).then((response) => {
			return response.data
		}).catch(err => {
			this.setState({error: err});
		});
console.log(test);
	}

	render() {
		return (
			 <div className="center">
			 <div className="text-center">
			 
			 <form className="form-signin" onSubmit={this.handleSubmit}>
			 <h2 className="h3 mb-3 font-weight-normal">Congratulations! Your account has been activated and email is verified</h2>
			 <br/>
			   
			  
			   
				
			   <button className="btn btn-primary" to="/login">Login</button>
			 </form>
		
			 </div>
		   </div>
		)
	}

}

const mapState = (state) => {
	console.log(state)
	
	return {
		allUsers: state.allUsers,
	  };
	
  };
  const mapDispatch = (dispatch, ownProps) => {
	return {
	  fetchAllUsers: () => dispatch(fetchAllUsersThunk()),
	  
	  addUser: (User) => dispatch(addUserThunk(User, ownProps)),
	  addUserEntity: (userEntity) => dispatch(addUserEntityThunk(userEntity, ownProps)),
	};
  };
  
export default connect(mapState, mapDispatch)(ConfirmAccount);
