import React from 'react';

import axios from "axios";

class UploadFile extends React.Component {
	
	constructor(props) {
		super(props);
		this.state = {file: '', message: ''};
	}
	
	onFileChange = (event) => {
		this.setState({
			file: event.target.files[0]
		});
	}
	
	uploadFileData = (event) => {
		event.preventDefault();
		this.setState({message: ''});
		var fileUpload;
		let data = new FormData();
		data.append('file', this.state.file);
		console.log(this.state.file);
		for(var pair of data.entries()){
			console.log(pair[0]+','+pair[1]);
			fileUpload += pair[1];
		};
		const credentials = {
			username: "sneka",
			password: "1234",
		  };
		  let token;
		console.log(fileUpload);
		axios.post("http://localhost:8080/authenticate", credentials)
      .then((response) => {
		token  = response.data.token;
        localStorage.setItem("jwtToken", token);
       
      })
		fetch(`http://localhost:8080/upload?file=${fileUpload}`, {
			method: 'POST',
			headers: {
				'Authorization': "Bearer "+token,
				
			}
	
		}).then(response => {

			this.setState({message: "File successfully uploaded"});
		}).catch(err => {
			this.setState({error: err});
		});

	}
	
	render() {
		return (
			<div id="container">
			
				<h3>Upload a File</h3>
				<h4>{this.state.message}</h4>
				<input onChange={this.onFileChange} type="file"></input>
				<button disabled={!this.state.file} onClick={this.uploadFileData}>Upload</button>
			</div>
		)
	}

}

export default UploadFile;
