import React from "react";
import PropTypes from "prop-types";
import Button from '@material-ui/core/Button';
import PhotoCamera from '@material-ui/icons/PhotoCamera';
import IconButton from '@material-ui/core/IconButton';

const VerificationView = (props) => {
  return (
    <div className="center">
      <div className="text-center">
      
      <form className="form-signin" onSubmit={props.handleSubmit}>
      <h2 className="h3 mb-3 font-weight-normal">Verification Process</h2>
      <div style={{
      display: 'flex',
      margin: 'auto',
      width: 400,
      flexWrap: 'wrap',
    }}>
      <div style={{ width: '100%', float: 'left' }}>
        <h3>How to use create button to choose file in ReactJS?</h3> <br />
      </div>
      <input
        type="file"
        accept="image/*"
        style={{ display: 'none' }}
        id="contained-button-file"
      />
      <label htmlFor="contained-button-file">
        <Button variant="contained" color="primary" component="span">
          Upload
        </Button>
      </label>
      <h3>  OR  </h3>
      <input accept="image/*" id="icon-button-file"
        type="file" style={{ display: 'none' }} />
      <label htmlFor="icon-button-file">
        <IconButton color="primary" aria-label="upload picture"
        component="span">
          <PhotoCamera />
        </IconButton>
      </label>
    </div>
      <br/>
        <div>
          aadhar:{" "}
          <input
          className="form-control"
            value={props.aadhar}
            name="aadhar"
            onChange={props.handleChange}
            required
          ></input>
        </div>
        <div>
          email_id:{" "}
          <input
          className="form-control"
            value={props.email_id}
            type="text"
            name="email_id"
            onChange={props.handleChange}
            required
          ></input>
        </div>
        <br/>
        <button className="btn btn-primary">Verify</button>
      </form>
      
      </div>
    </div>
  );
};

VerificationView.propTypes = {
  handleSubmit: PropTypes.func.isRequired,
  handleChange: PropTypes.func.isRequired,
  aadharnumber: PropTypes.string.isRequired,
  phonenumber: PropTypes.string.isRequired,
};

export default VerificationView;
